mapper.select:
SELECT * FROM  mi_dct_listar_docentes_nombres(#{nombres})
AS (id_docente integer, categoria dtexto2, id_persona integer, nombre_completo text,
paterno dtexto2, materno dtexto2, nombres dtexto, dip dtexto2);

SELECT * FROM  mi_dct_listar_docentes_dip(#{dip})
AS (id_docente integer, categoria dtexto2, id_persona integer, nombre_completo text, 
paterno dtexto2, materno dtexto2, nombres dtexto, dip dtexto2);

SELECT * FROM mi_dct_buscar_docente(#{id_docente})
AS (id_docente integer, id_persona dentero, apodo dtexto, clave dtexto2, categoria dtexto2, nombre_completo varchar, 
paterno dtexto2, materno dtexto2, nombres dtexto, dip dtexto2);

SELECT * FROM mi_dct_buscar_docentexdepartamento(#{id_docente},#{id_departamento})
AS (id_docente integer, id_persona dentero, apodo dtexto, clave dtexto2, categoria dtexto2, nombre_completo varchar, 
paterno dtexto2, materno dtexto2, nombres dtexto, dip dtexto2);

SELECT * FROM mi_dct_buscar_auxiliar(#{id_docente})
AS (id_docente dentero, id_persona dentero, apodo dtexto2, clave dtexto2, categoria dtexto2, nombre_completo dtexto, 
paterno dtexto2, materno dtexto2, nombres dtexto, dip dtexto2);
