mapper.select:

SELECT
estudiantes.id_estudiante, case when personas.paterno is null or personas.paterno = '' then personas.paterno|| ' ' ||personas.nombres else personas.paterno|| ' ' ||personas.materno|| ' ' ||personas.nombres end as nombre_completo, fcl_programas_desconcentrados.sede_desconcentrada
FROM
estudiantes
INNER JOIN personas ON estudiantes.id_persona = personas.id_persona
INNER JOIN est_programaciones ON est_programaciones.id_estudiante = estudiantes.id_estudiante
INNER JOIN fcl_programas_desconcentrados ON fcl_programas_desconcentrados.id_desconcentrado =est_programaciones.id_desconcentrado
WHERE 
est_programaciones.id_materia = #{id_materia}
AND est_programaciones.id_grupo =#{id_grupo}
AND est_programaciones.id_tipo_evaluacion = #{id_tipo_evaluacion}
AND est_programaciones.gestion =#{gestion}
AND est_programaciones.periodo = #{periodo}
AND est_programaciones.id_estado = 'A'

SELECT * FROM mi_est_listar_estudiantes_nombres(#{nombres}, #{id_programa})
AS (id_estudiante integer, id_persona integer, id_programa dentero, id_plan dtexto,nombre_completo varchar, paterno dtexto2, 
materno dtexto2, nombres dtexto, dip dtexto2, programa dtexto);

SELECT * FROM mi_est_listar_estudiantes_nombres_accesos(#{nombres}, #{id_universidad}, #{id_facultad}, #{id_programa})
AS (id_estudiante integer, id_persona integer, id_programa dentero, id_plan dtexto,nombre_completo text, paterno dtexto2, 
materno dtexto2, nombres dtexto, dip dtexto2, programa dtexto, id_tipo_estudiante dentero, tipo_estudiante dtexto);

SELECT * FROM mi_est_listar_estudiantes_dip_accesos(#{dip}, #{id_universidad}, #{id_facultad}, #{id_programa})
AS (id_estudiante integer, id_persona integer, id_programa dentero, id_plan dtexto, nombre_completo text, paterno dtexto2, 
materno dtexto2, nombres dtexto, dip dtexto2, programa dtexto, id_tipo_estudiante dentero, tipo_estudiante dtexto);

SELECT * FROM mi_est_listar_estudiantes_nombres(#{nombres})
